var wordsToType=document.querySelector("span[words]").getAttribute("words").split(","),typer=document.querySelector("span[words]"),typingSpeed=(parseInt(typer.getAttribute("typing-speed"))||70),typingDelay=(parseInt(typer.getAttribute("typing-delay"))||700);var currentWordIndex=0,currentCharacterIndex=0;function type(){var a=wordsToType[currentWordIndex%wordsToType.length];if(currentCharacterIndex<a.length){typer.innerHTML+=a[currentCharacterIndex++];setTimeout(type,typingSpeed)}else{setTimeout(erase,typingDelay)}}function erase(){var a=wordsToType[currentWordIndex%wordsToType.length];if(currentCharacterIndex>0){typer.innerHTML=a.substr(0,--currentCharacterIndex-1);setTimeout(erase,typingSpeed)}else{currentWordIndex++;setTimeout(type,typingDelay)}}window.onload=function(){type()};